var link=document.querySelector(".button-footer");var popup=document.querySelector(".modal");var close=popup.querySelector(".modal-close");var form=popup.querySelector("form");var login=popup.querySelector("[name=text]");var email=popup.querySelector("[name=email]");var isStorageSupport=!0;var storage="";try{storage=localStorage.getItem("login")}catch(err){isStorageSupport=!1}
link.addEventListener("click",function(evt){evt.preventDefault();popup.classList.add("modal-show");if(storage){login.value=storage;password.focus()}else{login.focus()}});close.addEventListener("click",function(evt){evt.preventDefault();popup.classList.remove("modal-show");popup.classList.remove("modal-error");popup.classList.remove("modal-error-toggle")});form.addEventListener("submit",function(evt){if(!login.value||!email.value){evt.preventDefault();popup.classList.add("modal-error");popup.classList.toggle("modal-error-toggle")}else{if(isStorageSupport){localStorage.setItem("login",login.value)}}});window.addEventListener("keydown",function(evt){if(evt.keyCode===27){evt.preventDefault();if(popup.classList.contains("modal-show")){popup.classList.remove("modal-show")}}})